    expected_tanker_volume INTEGER NOT NULL,
    direct_inline_sewage INTEGER NOT NULL,
    total_inlet_received INTEGER NOT NULL,
    total_treated_water INTEGER NOT NULL,
    total_tse_output INTEGER NOT NULL,
    income_from_tankers NUMERIC(6,1) NOT NULL,
    saving_from_tse INTEGER NOT NULL,
    total_saving_income INTEGER NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- Step 2: Create indexes for better performance
CREATE INDEX idx_stp_date ON stp_daily_records(date);
